    //     dispatch(setCurrentPage(page));
    //   } else {
    //     // if there is no page for today, create a new page
    //     dispatch(addPage(today));
    //   }
    // }

    // dispatch(getTodaysPage());
    loadTodaysPage();
  }, [dispatch]);

  return (
    <div>
      <Page />
    </div>
  );
}

export default App;
