import { useEffect } from "react";
import { Page } from "./components/Page";
import { useDispatch, useSelector } from "react-redux";
import { setCurrentPage } from "./reducers/PageReducer";
import { addPage } from "./reducers/JournalReducer";
import { getFormattedDate } from "./utils/getFormattedDate";
import { gql, useQuery } from "@apollo/client";
import { FIND_PAGE, ADD_PAGE } from "./utils/queries";

function App() {
  const dispatch = useDispatch();

  const today = getFormattedDate();
  const todaysPage = useQuery(FIND_PAGE, {
    variables: {
      month: today.month,
      dayNum: today.day.number,
      year: today.year,
    },
  });

  useEffect(() => {
    // if (todaysPage.data.findPage === null) {
    //   // add page
    // } else {
    //   // set todays page to current page
    //   dispatch(setCurrentPage(todaysPage.data.findPage));
    // }
    if (todaysPage.loading === false && todaysPage.data.findPage === null) {
      dispatch(addPage(today));
    } else if (
      todaysPage.loading === false &&
      todaysPage.data.findPage !== null
    ) {
      dispatch(setCurrentPage(todaysPage.data));
    }
  }, [todaysPage]);

  return (
    <div>
      <Page />
    </div>
  );
}

export default App;
