import { useLazyQuery, useQuery } from "@apollo/client";
import React, { useEffect, useState } from "react";
import { FC } from "react";
// @ts-ignore
import { GET_NEXT_PAGE, GET_PREVIOUS_PAGE } from "../../utils/queries";
import { useDispatch, useSelector } from "react-redux";
// @ts-ignore
import { setCurrentPage } from "../../reducers/PageReducer";
import { getFormattedDate } from "../../utils/getFormattedDate";

interface NavArrowProps {}

export const NavArrow: FC<NavArrowProps> = () => {
  const [onLastPage, setOnLastPage] = useState(false);
  const [onFirstPage, setOnFirstPage] = useState(false);

  const dispatch = useDispatch();

  const today = getFormattedDate();

  // const availablePages = useQuery(GET_AVAILABLE_PAGES);
  const currentPage = useSelector((state: any) => state.currentPage);

  const [getNextPage] = useLazyQuery(GET_NEXT_PAGE, {
    variables: { id: currentPage.id },
    onCompleted: (data) => {
      // if (data.getNextPage) {
      //   console.log(data.getNextPage);
      //   if (data.getNextPage.isEnd) {
      //     setOnLastPage(true);
      //   }
      //   dispatch(setCurrentPage(data.getNextPage.page));
      // }
      console.log(data.getNextPage);
      if (data.getNextPage && data.getNextPage.page !== null) {
        if (data.getNextPage.isEnd) {
          setOnLastPage(true);
        }
        dispatch(setCurrentPage(data.getNextPage.page));
      }
    },
  });

  const [getPreviousPage] = useLazyQuery(GET_PREVIOUS_PAGE, {
    variables: { id: currentPage.id },
    onCompleted: (data) => {
      // if (data.getPreviousPage) {
      //   console.log(data.getPreviousPage);
      //   if (data.getPreviousPage.isEnd) {
      //     setOnFirstPage(true);
      //   }
      //   dispatch(setCurrentPage(data.getPreviousPage.page));
      // }
      console.log(data.getPreviousPage);
      if (data.getPreviousPage && data.getPreviousPage.page !== null) {
        if (data.getPreviousPage.isEnd) {
          setOnFirstPage(true);
        }
        dispatch(setCurrentPage(data.getPreviousPage.page));
      }
    },
  });

  return (
    <div>
      <button onClick={() => getPreviousPage()} disabled={onFirstPage}>
        get previous
      </button>
      <button onClick={() => console.log(currentPage)}>current</button>
      <button onClick={() => getNextPage()} disabled={onLastPage}>
        get next
      </button>
    </div>
  );
};
